  getField('BTN_REVISAR').onclick = function() {
  var numero = getField("TXT_NO_HISTORIA").value;
    
     if(numero == ""){
      alert("Digite el número de documento");
      return false;
     }
   
   buscarFormulas();
 }
  
function buscarFormulas(){
   var q = getField('TXT_NO_HISTORIA').value;
    
   var rows;
   //clearGrid();
  
   if (window.XMLHttpRequest) // code for IE7+, Firefox, Chrome, Opera, Safari
     xmlhttp=new XMLHttpRequest();
   else // code for IE5, IE6
     xmlhttp=new ActiveXObject("Microsoft.XMLHTTP");
    //alert(q);
   xmlhttp.onreadystatechange=function(){
     //alert(xmlhttp.status); aqui hace tal cosa
     if(xmlhttp.readyState==4 && xmlhttp.status==200){
       //alert(xmlhttp.responseText);
       var rows = eval('(' + xmlhttp.responseText + ')');
       //var rows = '(' + xmlhttp.responseText + ')';
       
       if(rows.length == 0){
         alert("El paciente no tiene formulas médicas pendientes.");
         return false;
       }
       
       //inicio--------------------------------------------------------------------------------------------------------------------
       for (var i = 0; i < rows.length; i++){
         if((i+1)!=1){
            getObject("GRL_FORMULAS").addGridRow();
         }
         getGridField("GRL_FORMULAS",(i+1),"TXT_NUMERO").value = (i+1);
         getGridField("GRL_FORMULAS",(i+1),"TXT_CASO").value = rows[i].caso;
         getGridField("GRL_FORMULAS",(i+1),"TXT_FECHA").value = rows[i].fecha_cita;
         getGridField("GRL_FORMULAS",(i+1),"TXT_ATENCION").value = rows[i].descripcion;
         getGridField("GRL_FORMULAS",(i+1),"TXT_PROFESIONAL").value = rows[i].profesional;
       }
       //fin--------------------------------------------------------------------------------------------------------------------
       
     }
   }
   xmlhttp.open("POST", "/buscarFormulas.php?q="+q, false);
   xmlhttp.send();  
 }

 function clearGrid() {
   var grd = getObject('GRL_FORMULAS');
   var i = Number_Rows_Grid('GRL_FORMULAS', "TXT_NUMERO");
   for (i; i > 1; i--) {
       grd.deleteGridRow(i, true);
   }
   //The first row can't be deleted, so clear the fields in the first row:
   for (i = 0; i < grd.aFields.length; i++) {
       getGridField('GRL_FORMULAS', 1, grd.aFields[i].sFieldName).value = "";
   }
 return false;
}]]></val_revisar>
