function doQuery(){
    var rows;
    //clearGrid();
    
    if (window.XMLHttpRequest) // code for IE7+, Firefox, Chrome, Opera, Safari
      xmlhttp=new XMLHttpRequest();
    else // code for IE5, IE6
      xmlhttp=new ActiveXObject("Microsoft.XMLHTTP");
    
    xmlhttp.onreadystatechange=function(){
      if(xmlhttp.readyState==4 && xmlhttp.status==200){
        
        var rows = eval('(' + xmlhttp.responseText + ')');
        
        if(rows.length == 0 || rows[0].usr_uid==""){
          alert("no hay datos.");
          limpiar();
          return false;
        }
        else{
        //inicio--------------------------------------------------------------------------------------------------------------------
        for (var i = 0; i < rows.length; i++){
          getField("OCU_M_ANTES").value = rows[i].usr_uid;
          getField("DRP_GU_A_TIPODOC").value = rows[i].tipo_doc;
          getField("TXT_GU_A_NUMDOC").value = rows[i].num_doc;
          getField("TXT_GU_A_NUMDOC").value = rows[i].num_doc;
          getField("TXT_GU_A_EMAIL").value = rows[i].USR_USERNAME;
          getField("TXT_GU_A_NOMBRE").value = rows[i].USR_FIRSTNAME;
          getField("TXT_GU_A_APELLIDO").value = rows[i].USR_LASTNAME;
          getField("DRP_GU_AC").value = rows[i].USR_STATUS;
          //getField("TXT_GU_A_APELLIDO").value = rows[i].USR_LASTNAME;
          if(getField("OCU_A").value="1"){
            getField("DRP_GU_A_ESPECIALIDAD").value = rows[i].esp;
            getField("TXT_GU_A_REGMEDICO").value = rows[i].reg_med;
            getField("TXT_GU_A_REGCONTROL").value = rows[i].reg_control;
          }
        }
        showRowById("DRP_GU_AC");
        showRowById("SUB_GU_DATOSG");
        showRowById("TXT_GU_A_NOMBRE");
        showRowById("TXT_GU_A_APELLIDO");
        showRowById("TXT_GU_A_EMAIL");
        showRowById("TXT_GU_A_PASSWORD");
        showRowById("TXT_GU_A_PASSWORD2");
        //showRowById("TXT_GU_TIPODOC"); 
        showRowById("DRP_GU_A_TIPODOC");
        showRowById("TXT_GU_A_NUMDOC");
        showRowById("FIL_GU_A_FIRMA");
        showRowById("BTN_GU_A_ENVIAR");  
        if(getField("OCU_A").value="1"){
          showRowById("DRP_GU_A_ESPECIALIDAD");   
          showRowById("TXT_GU_A_REGMEDICO");
          showRowById("TXT_GU_A_REGCONTROL");         
        }
        //fin--------------------------------------------------------------------------------------------------------------------
       }
      }
     }
     var a=getField("TXT_CEDULA").value;
     var b=getField("OCU_A").value;
          
     xmlhttp.open("POST", "/LlenarModificar.php?r="+a+"&"+"s="+b, true);
     xmlhttp.send();  
}


