          return false;
        }

        //alert("La cita se ha agendado correctamente.");
        agregar_procedimiento();
      }
   }
   xmlhttp1.open("GET", "/validar_procedimiento.php?q="+x, true);
   xmlhttp1.send();
  
}


function agregar_procedimiento(){

  if(getField('TXT_PROCEDIMIENTO').value != '' && getField('TXT_COD_PRO').value == ''){
      
    var q = getField('TXT_PROCEDIMIENTO').value; 
    getField('TXT_COD_PRO').value = '';
    getField('TXT_COD_PRO_label').value = '';
    getField('TXT_PROCEDIMIENTO').value = '';
    getField('TXT_PROCEDIMIENTO_label').value = '';
  }

  if(getField('TXT_PROCEDIMIENTO').value == '' && getField('TXT_COD_PRO').value != ''){
      
    var q = getField('TXT_COD_PRO').value; 
    getField('TXT_COD_PRO').value = '';
    getField('TXT_COD_PRO_label').value = '';
    getField('TXT_PROCEDIMIENTO').value = '';
    getField('TXT_PROCEDIMIENTO_label').value = '';     
  }

  if(getField('TXT_PROCEDIMIENTO').value != '' && getField('TXT_COD_PRO').value != ''){
    return false;
  }

  var nRows = Number_Rows_Grid("GRL_PROCEDIMIENTOS","TXT_CONSE_PRO");
  
  for (var i = 1; i <= nRows; i++) {
      if (getGridField("GRL_PROCEDIMIENTOS",i,"TXT_CONSE_PRO").value == q){
              alert('Este procedimiento ya esta agregado.');
              return false;
      }
  }

  if (window.XMLHttpRequest) // code for IE7+, Firefox, Chrome, Opera, Safari
      xmlhttp=new XMLHttpRequest();
  else // code for IE5, IE6
      xmlhttp=new ActiveXObject("Microsoft.XMLHTTP");
 
  xmlhttp.onreadystatechange=function(){
      if(xmlhttp.readyState==4 && xmlhttp.status==200){
         
        var rows = eval('(' + xmlhttp.responseText + ')');
        for (var i = 0; i < rows.length; i++){
          if(Number_Rows_Grid("GRL_PROCEDIMIENTOS","TXT_COD_PRO") == 1 && getGridField("GRL_PROCEDIMIENTOS",1,"TXT_COD_PRO").value == ''){    
            getGridField("GRL_PROCEDIMIENTOS",1,"TXT_CONSE_PRO").value = rows[i].conse;
            getGridField("GRL_PROCEDIMIENTOS",1,"TXT_COD_PRO").value = rows[i].codigo;
            getGridField("GRL_PROCEDIMIENTOS",1,"TXT_PROCED").value = rows[i].descripcion;
          }
          else{
            getObject("GRL_PROCEDIMIENTOS").addGridRow();
            var n = Number_Rows_Grid("GRL_PROCEDIMIENTOS","TXT_COD_PRO");    
            getGridField("GRL_PROCEDIMIENTOS",n,"TXT_CONSE_PRO").value = rows[i].conse;
            getGridField("GRL_PROCEDIMIENTOS",n,"TXT_COD_PRO").value = rows[i].codigo;
            getGridField("GRL_PROCEDIMIENTOS",n,"TXT_PROCED").value = rows[i].descripcion;
          }
        }
      }
  }
  xmlhttp.open("POST", "/buscarProcedimiento.php?q="+q, true);
  xmlhttp.send();
  return false;
}]]></agregar>
