getField("BTN_CONTINUAR").form.onsubmit = function(){
  
  var fecha = getField("DATE_FECHA").value;
  var pa = getField("TXT_EXA_PA").value;
  var fc = getField("TXT_EXA_FC").value;  
  var fr = getField("TXT_EXA_FR").value;
  var t = getField("TXT_EXA_T").value;
  var sat = getField("TXT_EXA_SAT").value;
  var eg = getField("TXT_EXA_EG").value;
  var peso = getField("TXT_EXA_PESO").value;
  var talla = getField("TXT_EXA_TALLA").value;
  var imc = getField("TXT_EXA_IMC").value;
  var clasificacion = getField("DRP_CLASIFICACION").value;
  var sintomas = getField("TXT_SINTOMAS").value;
  var sexo = getField("TXT_SEXO").value;
  
  //alert();
  
  if (pa == "") {
    alert('Por favor ingrese la presion arterial.');
    return false;
  }
  if (fc == "") {
    alert('Por favor, ingrese la frecuencia cardiaca.');
    return false;
  }  
  if (fr == "") {
    alert('Por favor, ingrese la frecuencia respiratoria.');
    return false;
  }
  if (t == "") {
    alert('Por favor, ingrese la temperatura');
    return false;
  } 
 
  if (eg != "" && sexo == "Masculino") {
    alert('La edad gestacional no corresponde al sexo del paciente.');
    return false;
  }
  
  if (peso == "") {
    alert('Por favor, ingrese el peso.');
    return false;
  }
  if (talla == "") {
    alert('Por favor, ingrese la talla.');
    return false;
  }  
  if (sintomas == "") {
    alert('Por favor, ingrese los sintomas');
    return false;
  }
  if (clasificacion == "99") {
    alert('Por favor seleccione una clasificación.');
    return false;
  }
  
  var dia = dia_semana(fecha);
  
  var f=new Date();
  var hora = f.getHours();
  var clasificacion = getField("DRP_CLASIFICACION").value;  
  alert(dia+' Hora: '+hora);
  if(dia== 'Lunes' || dia== 'Martes' || dia== 'Miércoles' || dia== 'Jueves'){
    if(clasificacion == '1' || clasificacion == '2' || clasificacion == '3'){
      getField("TXT_DERIVAR").value = '0';
    }
    else{
      getField("TXT_DERIVAR").value = '1';
    }
  }
  else if(dia== 'Viernes'){
    
    if(hora < 16){
        if(clasificacion == '1' || clasificacion == '2' || clasificacion == '3'){
          getField("TXT_DERIVAR").value = '0';
        }
        else{
          getField("TXT_DERIVAR").value = '1';
        }      
    }
    else{
      getField("TXT_DERIVAR").value = '0';
    }
  }
  else{
    getField("TXT_DERIVAR").value = '0';
  }
  alert(getField("TXT_DERIVAR").value);
  return false;
  
}
  
  
