var changeDrop = function () {
   var clasificacion = getField("DRP_CLASIFICACION").value;
  
  if(clasificacion == '1'){
    getField("DRP_CLASIFICACION").style.backgroundColor = "gray";
    getField("DRP_CLASIFICACION").style.borderColor = "black";
    return false;
  }
  if(clasificacion == '2'){
    getField("DRP_CLASIFICACION").style.backgroundColor = "pink";
    getField("DRP_CLASIFICACION").style.borderColor = "red";
    return false;
  }
  if(clasificacion == '3'){
    getField("DRP_CLASIFICACION").style.backgroundColor = "yellow";
    getField("DRP_CLASIFICACION").style.borderColor = "orange";
    return false;
  }
  if(clasificacion == '4'){
    getField("DRP_CLASIFICACION").style.backgroundColor = "green";
    getField("DRP_CLASIFICACION").style.borderColor = "green";
    return false;
  }
  if(clasificacion == '5'){
    getField("DRP_CLASIFICACION").style.backgroundColor = "blue";
    getField("DRP_CLASIFICACION").style.borderColor = "blue";
    return false;
  }
};
var dynaformOnload = function() { // calling the function
   leimnud.event.add(getField('DRP_CLASIFICACION'), 'change', changeDrop ); 
   changeDrop();
};

getField("BTN_CONTINUAR").form.onsubmit = function(){
  
  //Variables campos
  var VAR_TXT_EXA_PA= getField("TXT_EXA_PA").value;
  var VAR_TXT_EXA_FC= getField("TXT_EXA_FC").value;
  var VAR_TXT_EXA_FR= getField("TXT_EXA_FR").value;
  var VAR_TXT_EXA_T= getField("TXT_EXA_T").value;
  var VAR_TXT_EXA_SAT= getField("TXT_EXA_SAT").value;
  var VAR_TXT_EXA_EG= getField("TXT_EXA_EG").value;
  var VAR_TXT_EXA_PESO= getField("TXT_EXA_PESO").value;
  var VAR_TXT_EXA_TALLA= getField("TXT_EXA_TALLA").value;
  ////
  var VAR_TXT_SINTOMAS= getField("TXT_SINTOMAS").value;
  ///
  var sexo = getField("TXT_SEXO").value;
  var clasificacion = getField("DRP_CLASIFICACION").value;
  
  //Variables Expresiones Regulares
  var EXP_OBS= /^[a-zA-Z0-9\s]*$/;
  var TEXTO = /^[áéíúóñÑaa-zA-Z0-9\s.-;,:]{2,600}$/;
  var DECIMAL = /^([1-9]+|0)([.]\d+)?$/;
  
  //Validaciones
  if(  !DECIMAL.test(VAR_TXT_EXA_PA) 
    || !DECIMAL.test(VAR_TXT_EXA_FC)
    || !DECIMAL.test(VAR_TXT_EXA_FR)
    || !DECIMAL.test(VAR_TXT_EXA_T)
    || !DECIMAL.test(VAR_TXT_EXA_SAT)
    || !DECIMAL.test(VAR_TXT_EXA_EG)
    || !DECIMAL.test(VAR_TXT_EXA_PESO)
    || !DECIMAL.test(VAR_TXT_EXA_TALLA)){    
    alert("Solo se admiten numeros enteros o numeros decimales con . en los respectivos campos");
    return false;
  } 
  
  if(VAR_TXT_EXA_EG!= "" && sexo == "Masculino") {
    alert('La edad gestacional no corresponde al sexo del paciente.');
    return false;
  }
  
  if (clasificacion == "99") {
    alert('Por favor seleccione una clasificación.');
    return false;
  }
  
  if(!TEXTO.test(VAR_TXT_SINTOMAS)){
    alert("Solo se permiten letras, numeros y espacios en el campo Sintomas");
    return false;
  }
  
  //Confirmacion de envio
  var res = confirm("Si no esta seguro presione CANCELAR, de lo contrario presione ACEPTAR para continuar.");
  if (res == true) return true;
  else return false;
}
  
var imc = function () {
   var peso = getField("TXT_EXA_PESO").value;
   var talla = getField("TXT_EXA_TALLA").value;
  //alert('hola2');
  
  if(peso != '' && !isNaN(peso) && talla != '' && !isNaN(talla)){
    getField("TXT_EXA_IMC").value = peso/(talla*talla);
    return false;
  }
  else{
    getField("TXT_EXA_IMC").value = '0';
    return false;
  }  
};
var dynaformOnload = function() { // calling the function
   leimnud.event.add(getField('TXT_EXA_PESO'), 'change',imc);
   leimnud.event.add(getField('TXT_EXA_TALLA'), 'change',imc);
  //alert('hola');
   imc();
};]]></JS_TRIAJE>
