var imc = function () {
   var peso = getField("TXT_PESO_MADRE").value;
   var talla = getField("TXT_TALLA_MADRE").value;
  //alert('hola2');
  
  if(peso != '' && !isNaN(peso) && talla != '' && !isNaN(talla)){
    getField("TXT_IMC").value = peso/(talla*talla);
    return false;
  }
  else{
    getField("TXT_IMC").value = '0';
    return false;
  }  
};

