  <validar_formulario type="javascript" meta=""><![CDATA[getField('BTN_AGREGAR').onclick = function() {
  
  if(getField('TXT_DESC_LAB').value == '' && getField('TXT_COD_LAB').value == ''){
    return false;
  }
  else{    
    if(getField('TXT_DESC_LAB').value != '' && getField('TXT_COD_LAB').value == ''){
      var codigo = getField('TXT_DESC_LAB').value;
      var descripcion = getField('TXT_DESC_LAB_label').value;
      getField('TXT_DESC_LAB').value = '';
      getField('TXT_DESC_LAB_label').value = '';
    }
    if(getField('TXT_DESC_LAB').value == '' && getField('TXT_COD_LAB').value != ''){
      var descripcion = getField('TXT_COD_LAB').value;
      var codigo = getField('TXT_COD_LAB_label').value;
      getField('TXT_COD_LAB').value = '';
      getField('TXT_COD_LAB_label').value = '';
    }    
  }  

  var nRows = Number_Rows_Grid("GRL_LABORATORIOS", "TXT_COD_LAB");
  
  for (var i = 1; i <= nRows; i++) {
      if (getGridField("GRL_LABORATORIOS",i,"TXT_COD_LAB").value == codigo){
              alert('Este exámen de laboratorio ya esta agregado.');
              return false;
      }
  }
          
  if(Number_Rows_Grid("GRL_LABORATORIOS","TXT_COD_LAB") == 1 && getGridField("GRL_LABORATORIOS",1,"TXT_COD_LAB").value == ''){    
    getGridField("GRL_LABORATORIOS",1,"TXT_COD_LAB").value = codigo;
    getGridField("GRL_LABORATORIOS",1,"TXT_DESC_LAB").value = descripcion;
  }
  else{
    getObject("GRL_LABORATORIOS").addGridRow();
    var n = Number_Rows_Grid("GRL_LABORATORIOS","TXT_COD_LAB");    
    getGridField("GRL_LABORATORIOS",n,"TXT_COD_LAB").value = codigo;
    getGridField("GRL_LABORATORIOS",n,"TXT_DESC_LAB").value = descripcion;
  }  
  return false;
}]]></validar_formulario>
