   changeDrop_DRP_ANOMALIAS_CONGEN();

   idrn();

};

var idrn = function () {
   var TXT_ID_RN  =       getField("TXT_ID_RN").value;
   var ENTERO = /^(([1-9]0*)+|0){0,2}$/;
  //alert('hola2');
  
  if(ENTERO.test(TXT_ID_RN)){
    getField("TXT_NUM_RN").value = getField("TXT_NO_HISTORIA").value+TXT_ID_RN;
    return false;
  }
  else{
    getField("TXT_NUM_RN").value = '0';
    return false;
  }  
};]]></validar_formulario_ya_nacio>
