  <validar_formulario_finalizar_proceso type="javascript" meta=""><![CDATA[getField ("BTN_ENVIAR").form.onsubmit = function (){

  var TEXTO1000 = /^[áéíúóñÑaa-zA-Z0-9\s.-;,:()]{2,1000}$/;

  var area_completar_examen_nacido=getField("AREA_COMPLETAR_EXAMEN_NACIDO").value;
  
  var area_diagnostico_nacido=getField("AREA_DIAGNOSTICO_NACIDO").value;
  var area_volver_servicio=getField("AREA_VOLVER_SERVICIO").value;
  var area_volver_control_nacido=getField("AREA_VOLVER_CONTROL_NACIDO").value;
  var area_volver_consulta_sano=getField("AREA_VOLVER_CONSULTA_SANO").value;
  var area_referido_consulta=getField("AREA_REFERIDO_CONSULTA").value;
  var area_medidas_especificas=getField("AREA_MEDIDAS_ESPECIFICAS").value;
  var area_crecimiento_desarrollo=getField("AREA_CRECIMIENTO_DESARROLLO").value;
  var area_recomendaciones_buen_trato=getField("AREA_RECOMENDACIONES_BUEN_TRATO").value;
  var area_otras_recomendaciones=getField("AREA_OTRAS_RECOMENDACIONES").value;
  var area_tratar=getField("AREA_TRATAR").value;

  if (!TEXTO1000.test(area_completar_examen_nacido)) { alert("Por favor digite correctamente examen recien nacido."); setFocus(getField('AREA_COMPLETAR_EXAMEN_NACIDO')); return false;}
  if (!TEXTO1000.test(area_diagnostico_nacido)) { alert("Por favor digite correctamente diagnostico recien nacido."); setFocus(getField('AREA_DIAGNOSTICO_NACIDO')); return false;}
  if (!TEXTO1000.test(area_volver_servicio)) { alert("Por favor digite correctamente volver a servicio."); setFocus(getField('AREA_VOLVER_SERVICIO')); return false;}
  if (!TEXTO1000.test(area_volver_control_nacido)) { alert("Por favor digite correctamente volver a control."); setFocus(getField('AREA_VOLVER_CONTROL_NACIDO')); return false;}
  if (!TEXTO1000.test(area_volver_consulta_sano)) { alert("Por favor digite correctamente volver a consulta."); setFocus(getField('AREA_VOLVER_CONSULTA_SANO')); return false;}
  if (!TEXTO1000.test(area_referido_consulta)) { alert("Por favor digite correctamente referido consulta."); setFocus(getField('AREA_REFERIDO_CONSULTA')); return false;}
  if (!TEXTO1000.test(area_medidas_especificas)) { alert("Por favor digite correctamente medidas especificas."); setFocus(getField('AREA_MEDIDAS_ESPECIFICAS')); return false;}
  if (!TEXTO1000.test(area_crecimiento_desarrollo)) { alert("Por favor digite correctamente crecimiento desarrollo."); setFocus(getField('AREA_CRECIMIENTO_DESARROLLO')); return false;}
  if (!TEXTO1000.test(area_recomendaciones_buen_trato)) { alert("Por favor digite correctamente recomendacion buen trato."); setFocus(getField('AREA_RECOMENDACIONES_BUEN_TRATO')); return false;}
  if (!TEXTO1000.test(area_otras_recomendaciones)) { alert("Por favor digite correctamente otras recomendaciones."); setFocus(getField('AREA_OTRAS_RECOMENDACIONES')); return false;}
  if (!TEXTO1000.test(area_tratar)) { alert("Por favor digite correctamente area a tratar."); setFocus(getField('AREA_TRATAR')); return false;}
  
   //Confirmacion de envio
  var res = confirm("Si no esta seguro presione CANCELAR, de lo contrario presione ACEPTAR para continuar.");
  if (res == true) return true;
  else return false;
  
  }]]></validar_formulario_finalizar_proceso>
</dynaForm>
