  if (!texto.test(YN_LENGUA_OBS) && YN_LENGUA==1) { alert("Por favor digite correctamente observacion de lengua.");    setFocus(getField('YN_LENGUA_OBS'));    return false;}
  if (!texto.test(YN_PISO_BOCA_OBS) && YN_PISO_BOCA==1) { alert("Por favor digite correctamente observacion de piso de bosa.");    setFocus(getField('YN_PISO_BOCA_OBS'));    return false;}
  if (!texto.test(YN_REBORDE_OBS) && YN_REBORDE==1) { alert("Por favor digite correctamente observacion de reborde.");    setFocus(getField('YN_REBORDE_OBS'));    return false;}
  if (!texto.test(YN_SALIVALES_OBS) && YN_SALIVALES==1) { alert("Por favor digite correctamente observacion de glandulas salivales.");    setFocus(getField('YN_SALIVALES_OBS'));    return false;}
  
  if (!texto.test(YN_CAM_FOR_OBS) && YN_CAM_FOR==1) { alert("Por favor digite correctamente observacion de cambio de forma.");    setFocus(getField('YN_CAM_FOR_OBS'));    return false;}
  if (!texto.test(YN_CAM_TAM_OBS) && YN_CAM_TAM==1) { alert("Por favor digite correctamente observacion de cambio de tamaño.");    setFocus(getField('YN_CAM_TAM_OBS'));    return false;}
