  if (!texto.test(YN_ACC_PRE2_OBS)) { alert("Por favor digite correctamente observacion de practica de cepillado.");    setFocus(getField('YN_ACC_PRE2_OBS'));    return false;}
  if (!texto.test(YN_ACC_PRE3_OBS)) { alert("Por favor digite correctamente observacion de uso de seda dental.");    setFocus(getField('YN_ACC_PRE3_OBS'));    return false;}
  if (!texto.test(YN_ACC_PRE4_OBS)) { alert("Por favor digite correctamente observacion de aplicacion de fluor.");    setFocus(getField('YN_ACC_PRE4_OBS'));    return false;}
  if (!texto.test(YN_ACC_PRE5_OBS)) { alert("Por favor digite correctamente observacion de cambio de posicion.");    setFocus(getField('YN_ACC_PRE5_OBS'));    return false;}
  if (!texto.test(YN_ACC_PRE6_OBS)) { alert("Por favor digite correctamente observacion de sellantes.");    setFocus(getField('YN_ACC_PRE6_OBS'));    return false;}

