var changeDrop_DRP_ACTIVIDAD = function () {
   var valor = getField("DRP_ACTIVIDAD").value;
  
  if(valor == '0' || valor == '2' || valor == '3' || valor == '4'){
    getField("TXT_TRABAJO").value = '';
    disable(getField("TXT_TRABAJO"));
    return false;
  }
  if(valor == '1'){
    enable(getField("TXT_TRABAJO"));
    return false;
  }  
};

