function clearGrid() {
   var grd = getObject('GRL_CANCELAR_CITAS_MEDICAS');
   var i = Number_Rows_Grid('GRL_CANCELAR_CITAS_MEDICAS', "TXT_HORA");
   for (i; i > 1; i--) {
       grd.deleteGridRow(i, true);
   }
   //The first row can't be deleted, so clear the fields in the first row:
   for (i = 0; i < grd.aFields.length; i++) {
       getGridField('GRL_CANCELAR_CITAS_MEDICAS',1, grd.aFields[i].sFieldName).value = "";
   }
 return false;
}

function citas(){
  
  var q = getField("TXT_NO_HISTORIA").value;
  var rows;
   clearGrid();
    
   if (window.XMLHttpRequest) // code for IE7+, Firefox, Chrome, Opera, Safari
      xmlhttp=new XMLHttpRequest();
   else // code for IE5, IE6
      xmlhttp=new ActiveXObject("Microsoft.XMLHTTP");
 
   xmlhttp.onreadystatechange=function(){
      if(xmlhttp.readyState==4 && xmlhttp.status==200){
         
        var rows = eval('(' + xmlhttp.responseText + ')');

        var contador = Number_Rows_Grid("GRL_CANCELAR_CITAS_MEDICAS", "TXT_HORA");

        if(rows.length != 0){

            for (var i = 0; i < rows.length; i++){
              if(i != 0){
                getObject("GRL_CANCELAR_CITAS_MEDICAS").addGridRow();
              }
              getGridField("GRL_CANCELAR_CITAS_MEDICAS",i+1,"TXT_FECHA").value = rows[i].fecha;
              getGridField("GRL_CANCELAR_CITAS_MEDICAS",i+1,"TXT_HORA").value = rows[i].hora;
              getGridField("GRL_CANCELAR_CITAS_MEDICAS",i+1,"TXT_PACIENTE").value = rows[i].descripcion;
            }
        }
        return false;
      }
   }
   xmlhttp.open("POST", "/cancelarCitas.php?q="+q, false);
   xmlhttp.send();  
}]]></agenda>
