  <Js_Agudeza_visual type="javascript" meta=""><![CDATA[getField("BTN_ENVIAR").form.onsubmit = function(){

      var txt_mot_consulta    = getField("TXT_MOTIVO_CONSULTA").value;
      var txt_enfer_actual    = getField("TXT_ENFERMEDAD_ACTUAL").value;
      var txt_area_ojo_der    = getField("TXT_AREA_OJO_DER").value;
      var txt_area_ojo_izq    = getField("TXT_AREA_OJO_IZQ").value;
      var txt_area_conducta   = getField("TXT_AREA_CONDUCTA").value;
      var txt_area_obser_agudeza    = getField("TXT_AREA_OBSERVACIONES_AGUDEZA").value;
      
      
      var texto     = /^[áéíúóñÑa-zA-Z0-9\s\.\-\;\,\:\%]{10,600}$/;
      var texto1     = /^[áéíúóñÑa-zA-Z0-9\s\.\-\;\,\:\%]{0,600}$/;
      var texto_pa  = /([0-9]{2,3})+\/([0-9]{2,3})+$/;

      if(txt_mot_consulta == '' || !texto.test(txt_mot_consulta)){
        alert("Por favor ingrese el motivo de la consulta, debe tener máximo 600 caracteres alfanuméricos y/o los siguientes simbolos: . - ; , : %");
        setFocus(getField("TXT_MOTIVO_CONSULTA"));
        return false;
      }
  
      if(txt_enfer_actual == '' || !texto.test(txt_enfer_actual)){
        alert("Por favor ingrese la enfermedad actual, debe tener máximo 600 caracteres alfanuméricos y/o los siguientes simbolos: . - ; , : %");
        setFocus(getField("TXT_ENFERMEDAD_ACTUAL"));
        return false;
      }

      if(txt_area_ojo_der == '' || !texto.test(txt_area_ojo_der)){
        alert("Por favor ingrese el diagnostico del ojo derecho, debe tener máximo 600 caracteres alfanuméricos y/o los siguientes simbolos: . - ; , : %");
        setFocus(getField("TXT_AREA_OJO_DER"));
        return false;
      }
      
      if(txt_area_ojo_izq == '' || !texto.test(txt_area_ojo_izq)){
        alert("Por favor ingrese el diagnostico del ojo izquierdo, debe tener máximo 600 caracteres alfanuméricos y/o los siguientes simbolos: . - ; , : %");
        setFocus(getField("TXT_AREA_OJO_IZQ"));
        return false;
      }

      if(txt_area_conducta == '' || !texto.test(txt_area_conducta)){
        alert("Por favor ingrese la conducta, debe tener máximo 600 caracteres alfanuméricos y/o los siguientes simbolos: . - ; , : %");
        setFocus(getField("TXT_AREA_CONDUCTA"));
        return false;
      }

      if(!texto1.test(txt_area_obser_agudeza)){
        alert("El campo observaciones, debe tener máximo 600 caracteres alfanuméricos y/o los siguientes simbolos: . - ; , : %");
        setFocus(getField("TXT_AREA_OBSERVACIONES_AGUDEZA"));
        return false;
      }
}//fin validacion boton
  
