  <JS_ODONTO type="javascript" meta=""><![CDATA[getField("BTN_CONTINUAR").form.onsubmit = function(){
  
  //Variables campos
  var TXT_MOTIVO_CONSULTA= getField("TXT_MOTIVO_CONSULTA").value;
  var TXT_ENF_ACTUAL= getField("TXT_ENF_ACTUAL").value;
  var TXT_ANT_OBS= getField("TXT_ANT_OBS").value;
  var TXT_ANT_FAM= getField("TXT_ANT_FAM").value;
  
  //Variables Expresiones Regulares
  var EXP_OBS= /^[a-zA-Z0-9\s]*$/;
  var TEXTO = /^[áéíúóñÑaa-zA-Z0-9\s.-;,:]{2,600}$/;
  
  if (!TEXTO.test(TXT_MOTIVO_CONSULTA)) { alert("Por favor digite correctamente el motivo de consulta.");    setFocus(getField('TXT_MOTIVO_CONSULTA'));    return false;}
  if (!TEXTO.test(TXT_ENF_ACTUAL)) { alert("Por favor digite correctamente enfermedad actual.");    setFocus(getField('TXT_ENF_ACTUAL'));    return false;}
  if (!TEXTO.test(TXT_ANT_OBS)) { alert("Por favor digite correctamente observaciones.");    setFocus(getField('TXT_ANT_OBS'));    return false;}
  if (!TEXTO.test(TXT_ANT_FAM)) { alert("Por favor digite correctamente antecedentes familiares.");    setFocus(getField('TXT_ANT_FAM'));    return false;} 
  
  //Confirmacion de envio
  var res = confirm("Si no esta seguro presione CANCELAR, de lo contrario presione ACEPTAR para continuar.");
  if (res == true) return true;
  else return false;
}
  
