  <GRL_DIAGNOSTICOS type="grid" xmlgrid="76976623957fc1274772f16008892513/46360729957fdb0ea0f08a4027167202" addrow="0" deleterow="1" required="0" readonly="0" optgroup="0"/>
  <validar_formulario type="javascript" meta=""><![CDATA[getField('BTN_CONTINUAR').form.onsubmit = function() {
 
  var nRows = Number_Rows_Grid("GRL_DIAGNOSTICOS", "TXT_COD_DIAG");
  
  if(nRows == 0){
    alert('Debe agregar almenos un diagnosticos.');
    return false;
  }
  
  for (var i = 1; i <= nRows; i++) {
      if (getGridField("GRL_DIAGNOSTICOS",i,"TXT_COD_DIAG").value == ""){
        alert('Debe agregar almenos un diagnosticos.');
        return false;
      }
  }

}
  

getField('BTN_AGREGAR').onclick = function() {
  
  if(getField('TXT_DESC_DIAG').value == '' && getField('TXT_COD_DIAG').value == ''){
    return false;
  }
  else{    
    if(getField('TXT_DESC_DIAG').value != '' && getField('TXT_COD_DIAG').value == ''){
      var codigo = getField('TXT_DESC_DIAG').value;
      var descripcion = getField('TXT_DESC_DIAG_label').value;
      getField('TXT_DESC_DIAG').value = '';
      getField('TXT_DESC_DIAG_label').value = '';
    }
    if(getField('TXT_DESC_DIAG').value == '' && getField('TXT_COD_DIAG').value != ''){
      var descripcion = getField('TXT_COD_DIAG').value;
      var codigo = getField('TXT_COD_DIAG_label').value;
      getField('TXT_COD_DIAG').value = '';
      getField('TXT_COD_DIAG_label').value = '';
    }    
  }  

  var nRows = Number_Rows_Grid("GRL_DIAGNOSTICOS", "TXT_COD_DIAG");
  
  if(nRows == 4){
    alert('Solo se permiten agregar 4 diagnosticos.');
    return false;
  }
  
  for (var i = 1; i <= nRows; i++) {
      if (getGridField("GRL_DIAGNOSTICOS",i,"TXT_COD_DIAG").value == codigo){
              alert('Este diagnostico ya esta agregado.');
              return false;
      }
  }
          
  if(Number_Rows_Grid("GRL_DIAGNOSTICOS","TXT_COD_DIAG") == 1 && getGridField("GRL_DIAGNOSTICOS",1,"TXT_COD_DIAG").value == ''){    
    getGridField("GRL_DIAGNOSTICOS",1,"TXT_COD_DIAG").value = codigo;
    getGridField("GRL_DIAGNOSTICOS",1,"TXT_DESC_DIAG").value = descripcion;
  }
  else{
    getObject("GRL_DIAGNOSTICOS").addGridRow();
    var n = Number_Rows_Grid("GRL_DIAGNOSTICOS","TXT_COD_DIAG");    
    getGridField("GRL_DIAGNOSTICOS",n,"TXT_COD_DIAG").value = codigo;
    getGridField("GRL_DIAGNOSTICOS",n,"TXT_DESC_DIAG").value = descripcion;
  }  
  return false;
}]]></validar_formulario>
