  <validar_formulario type="javascript" meta=""><![CDATA[getField('BTN_AGREGAR').onclick = function() {
  
  var numero = getField('TXT_DESC_MED').value;
  var descripcion = getField('TXT_DESC_MED_label').value;
  var codigo =  getField('TXT_CODIGO').value;
  var concentracion =  getField('TXT_CON_MED').value;
  var forma =  getField('TXT_FORMA').value;

  if(descripcion == ''){
    return false;
  }

  var nRows = Number_Rows_Grid("GRL_MEDICAMENTOS","TXT_DESC_MED");
  
  for (var i = 1; i <= nRows; i++) {
      if (getGridField("GRL_MEDICAMENTOS",i,"TXT_DESC_MED").value == descripcion){
              alert('Este medicamento ya esta agregado.');
              return false;
      }
  }
          
  if(Number_Rows_Grid("GRL_MEDICAMENTOS","TXT_DESC_MED") == 1 && getGridField("GRL_MEDICAMENTOS",1,"TXT_DESC_MED").value == ""){ 
    getGridField("GRL_MEDICAMENTOS",1,"TXT_CONSE_MED").value = numero;
    getGridField("GRL_MEDICAMENTOS",1,"TXT_DESC_MED").value = descripcion ;
    getGridField("GRL_MEDICAMENTOS",1,"TXT_COD_MED").value = codigo;
    getGridField("GRL_MEDICAMENTOS",1,"TXT_DESC_MED").value = descripcion ;
    getGridField("GRL_MEDICAMENTOS",1,"TXT_CON_MED").value = concentracion;
    getGridField("GRL_MEDICAMENTOS",1,"TXT_FORMA").value = forma;
   }
  
  else{       
    getObject("GRL_MEDICAMENTOS").addGridRow();
    var n = Number_Rows_Grid("GRL_MEDICAMENTOS","TXT_COD_MED");
    getGridField("GRL_MEDICAMENTOS",n,"TXT_CONSE_MED").value = numero;
    getGridField("GRL_MEDICAMENTOS",n,"TXT_COD_MED").value = codigo;
    getGridField("GRL_MEDICAMENTOS",n,"TXT_DESC_MED").value = descripcion ;
    getGridField("GRL_MEDICAMENTOS",n,"TXT_CON_MED").value = concentracion;
    getGridField("GRL_MEDICAMENTOS",n,"TXT_FORMA").value = forma;
  }
  getField('TXT_DESC_MED').value=" ";
  getField('TXT_DESC_MED').value=" ";
  getField('TXT_DESC_MED_label').value=" ";
  getField('TXT_CODIGO').value=" ";
  getField('TXT_CON_MED').value=" ";
  getField('TXT_FORMA').value=" ";
  return false;
}
  
  hidden(getField("DYN_FORWARD").parentNode);

getField('BTN_CONTINUAR').form.onsubmit = function() {
 
  var nRows = Number_Rows_Grid("GRL_MEDICAMENTOS", "TXT_CANT_MED");
  var texto = /^[áéíúóñÑa-zA-Z0-9\s\.\-\;\,\:\%]{10,600}$/;
    
  for (var i = 1; i <= nRows; i++) {
      if(!texto.test(getGridField("GRL_MEDICAMENTOS",i,"TXT_DOSIS_MED").value)){
        alert("La dosis de cada medicamento no puede estar vacia, debe contener caracteres alfanuméricos y/o los siguientes simbolos: . - ; , : %");
        return false;
      }


      if(i==1 && getGridField("GRL_MEDICAMENTOS",i,"TXT_DESC_MED").value != ''){
        if (getGridField("GRL_MEDICAMENTOS",i,"TXT_CANT_MED").value == "" || isNaN(getGridField("GRL_MEDICAMENTOS",i,"TXT_CANT_MED").value)){
          alert('Debe agregar la cantidad de cada medicamento en valores numéricos.');
          return false;
        }
      }
      else if(i!=1 && getGridField("GRL_MEDICAMENTOS",i,"TXT_DESC_MED").value != '' || isNaN(getGridField("GRL_MEDICAMENTOS",i,"TXT_CANT_MED").value)){
        if (getGridField("GRL_MEDICAMENTOS",i,"TXT_CANT_MED").value == ""){
          alert('Debe agregar la cantidad de cada medicamento en valores numéricos.');
          return false;
        }
      }
        
  }

}]]></validar_formulario>
