GRL_ESCALA_C.onchange = function(){  
  var nRows = Number_Rows_Grid("GRL_ESCALA_C", "TXT_ITEM");
  var total = 0;
  for (var i = 1; i <= nRows; i++) {
    if(getGridField("GRL_ESCALA_C", i, "YN_OPCION").value == "1")
    {
        total =0;
    }
    else if(getGridField("GRL_ESCALA_C", i, "YN_OPCION").value == "2")
    {
        total ++;
    }   
  }
  
  if(total >3){
      alert("Se ha detectado que en 3 items el resultado ha sido negativo. Este es el número maxímo de calificaciones negativas que debe recibir esta escala de desarrollo. Se recomienda detener la calificación de este grupo en este momento.");
  }  
}]]></arrow>
