function validar_cita(){
  
  var q = getField("TXT_NO_HISTORIA").value+'*-*'+getField("DRP_TIPO").value;
  
  var rows;
   
    
   if (window.XMLHttpRequest) // code for IE7+, Firefox, Chrome, Opera, Safari
      xmlhttp=new XMLHttpRequest();
   else // code for IE5, IE6
      xmlhttp=new ActiveXObject("Microsoft.XMLHTTP");
 
   xmlhttp.onreadystatechange=function(){
      if(xmlhttp.readyState==4 && xmlhttp.status==200){
         
        var rows = eval('(' + xmlhttp.responseText + ')');
        //getField("TXT_VALIDAR_CITA").value = rows;
        
        if(rows == '1'){
          alert('El sexo del paciente no aplica para la atención solicitada.');          
          return false;
        }
        if(rows == '2'){
          alert('La edad del paciente no aplica para la atención solicitada.');          
          return false;
        }
        if(rows != '0'){
          alert('El paciente aun no cumple con el tiempo requerido para solicitar cita para este servicio. Faltan '+ rows + 'días.');    
          return false;
        }

        return rows;
      }
   }
   xmlhttp.open("GET", "/validar_cita.php?q="+q, false);
   xmlhttp.send();  
}

