}

function clearGrid() {
   var grd = getObject('GRL_CITAS_MEDICAS');
   var i = Number_Rows_Grid('GRL_CITAS_MEDICAS', "TXT_HORA");
   for (i; i > 1; i--) {
       grd.deleteGridRow(i, true);
   }
   //The first row can't be deleted, so clear the fields in the first row:
   for (i = 0; i < grd.aFields.length; i++) {
       getGridField('GRL_CITAS_MEDICAS', 1, grd.aFields[i].sFieldName).value = "";
   }
 return false;
}

function revisarDia(){
   
  var q = getField("DRP_MED").value+'*-*'+getField('DRP_TIPO').value+'*-*'+getField("DATE_CITA").value;
   
   var rows;
   clearGrid();
   
   if (window.XMLHttpRequest) // code for IE7+, Firefox, Chrome, Opera, Safari
      xmlhttp=new XMLHttpRequest();
   else // code for IE5, IE6
      xmlhttp=new ActiveXObject("Microsoft.XMLHTTP");
 
   xmlhttp.onreadystatechange=function(){
      if(xmlhttp.readyState==4 && xmlhttp.status==200){
         
        var rows = eval('(' + xmlhttp.responseText + ')');
        //alert(rows.length);
        if(rows.length == 0){
           alert("El medico no ha registrado agenda de "+getField("DRP_TIPO").options[getField('DRP_TIPO').selectedIndex].text+" para este día.");
           return false;
        }
        var fecha_cita = getField("DATE_CITA").value.split("-"); 
        var opc=1;
        //inicio--------------------------------------------------------------------------------------------------------------------
        for (var i = 0; i < rows.length; i++){
          //var fecha_agenda = rows[i].fecha.split("-");
          
          var fecha_agenda = rows[i].fecha.split("-"); 
          if(fecha_agenda[0]==fecha_cita[0] && fecha_agenda[1]==fecha_cita[1] && fecha_agenda[2]==fecha_cita[2]){
            
            var uno=parseInt(rows[i].hora_inicio);
            var dos=parseInt(rows[i].hora_fin);
            var tres= dos-uno;

            /*if(Number_Rows_Grid("GRL_CITAS_MEDICAS", "TXT_HORA") == 1){
              var campos = (tres*3)+1;
            } 
            else{*/
              var campos = tres * 3;
            //}

            var h=uno; var m = '00';
            
            var cuatro = Number_Rows_Grid("GRL_CITAS_MEDICAS", "TXT_HORA");
            
            for(var x = cuatro ; x < (campos+cuatro); x++){
              
              if(x != 1){
                getObject("GRL_CITAS_MEDICAS").addGridRow();
              }
              enable(getGridField("GRL_CITAS_MEDICAS",x,"CHK_AGENDAR"));
              getGridField("GRL_CITAS_MEDICAS",x,"TXT_HORA").value = h+":"+m;
              if(m == '00'){m='20';}
              else if(m == '20'){m='40';}
              else if(m == '40'){m='00'; h++;}
            }
            opc=0;
          }
          
        }
        //fin--------------------------------------------------------------------------------------------------------------------
        if(opc==1){
          alert("El medico no ha registrado agenda de "+getField("DRP_TIPO").options[getField('DRP_TIPO').selectedIndex].text+" para este día.");
          return false;
        }
        
       citas();
      }
   }
   xmlhttp.open("POST", "/buscarAgenda.php?q="+q, true);
   xmlhttp.send();
}

function citas(){
  
  var q = getField("DATE_CITA").value+'*-*'+getField("DRP_MED").value+'*-*'+getField("DRP_TIPO").value;
  
  var rows;
   
    
   if (window.XMLHttpRequest) // code for IE7+, Firefox, Chrome, Opera, Safari
      xmlhttp=new XMLHttpRequest();
   else // code for IE5, IE6
      xmlhttp=new ActiveXObject("Microsoft.XMLHTTP");
 
   xmlhttp.onreadystatechange=function(){
      if(xmlhttp.readyState==4 && xmlhttp.status==200){
         
        var rows = eval('(' + xmlhttp.responseText + ')');
        //alert(rows);
        var contador = Number_Rows_Grid("GRL_CITAS_MEDICAS", "TXT_HORA");

        if(rows.length != 0){

            for (var i = 0; i < rows.length; i++){
              //alert(rows[i].nombre+'---'+rows[i].hora_cita);
              for(var x = 1 ; x <= contador; x++){
                  
                  if(getGridField("GRL_CITAS_MEDICAS",x,"TXT_HORA").value == rows[i].hora_cita ){                  
                    disable(getGridField("GRL_CITAS_MEDICAS",x,"CHK_AGENDAR"));                  
                    getGridField("GRL_CITAS_MEDICAS",x,"TXT_PACIENTE").value = rows[i].nombre;                    
                  }
                  else{                  
                    //enable(getGridField("GRL_CITAS_MEDICAS",x,"CHK_AGENDAR"));                               
                  }
              }
            }
        }
        return false;
      }
   }
   xmlhttp.open("GET", "/buscarCitas.php?q="+q, true);
   xmlhttp.send();  
}

function servicios(){

  getField('DRP_TIPO').length = 0;

  if(getField("TXT_EMPRESA").value == '' || getField("DRP_TIPO_FACTURACION").value == '99'){    
    return false;
  }

  var opt = document.createElement("OPTION");
          opt.text = 'Seleccione';
          opt.value = '99';          
          getField("DRP_TIPO").options.add(opt);
  
  var q = getField("DRP_TIPO_FACTURACION").value+'*-*'+getField("TXT_EMPRESA").value;
  //alert(q);
  if (window.XMLHttpRequest) // code for IE7+, Firefox, Chrome, Opera, Safari
      xmlhttp=new XMLHttpRequest();
  else // code for IE5, IE6
      xmlhttp=new ActiveXObject("Microsoft.XMLHTTP");
 
  xmlhttp.onreadystatechange=function(){
      if(xmlhttp.readyState==4 && xmlhttp.status==200){
         
        var rows = eval('(' + xmlhttp.responseText + ')');
        //alert(rows);
        for (var i = 0; i < rows.length; i++){
          var opt = document.createElement("OPTION");
          opt.text = rows[i].option;
          opt.value = rows[i].valor;          
          getField("DRP_TIPO").options.add(opt);
        }
      }
  }
  xmlhttp.open("GET", "/buscarServicios.php?q="+q, false);
  xmlhttp.send();
}getField('DRP_TIPO_FACTURACION').onchange = servicios;]]></JS_CITAS_MEDICAS>
